mapper.select:
SELECT COUNT(*) FROM tb_inventory_info

	name LIKE CONCAT('%', #{name},'%') OR  abbreviation LIKE CONCAT('%', #{name},'%')
	AND time >= #{beginTime}
	AND time <= #{endTime}



	name LIKE CONCAT('%', #{name},'%') OR  abbreviation LIKE CONCAT('%', #{name},'%')
	AND time >= #{beginTime}
	AND time <= #{endTime}

LIMIT #{offset}, #{size}
ORDER BY time DESC
