mapper.select:
SELECT COUNT(*) FROM tb_sell
JOIN tb_inventory_info ON tb_inventory_info.id = tb_sell.inventory_id
JOIN tb_customer_info ON tb_customer_info.id = tb_sell.customer_id

	tb_inventory_info.name = #{inventoryName}
	AND tb_customer_info.name = #{customerName}
	AND tb_sell.time > #{beginTime}
	AND tb_sell.time <= #{endTime}

SELECT tb_sell.id AS id, tb_inventory_info.name AS inventory_name, tb_customer_info.name AS customer_name, tb_sell.unit_cost AS unit_cost, tb_sell.amount AS amount, tb_sell.sum AS sum, tb_sell.time AS time, tb_sell.clearing_form AS clearing_form, tb_sell.handler AS handler FROM tb_sell JOIN tb_inventory_info ON tb_inventory_info.id = tb_sell.inventory_id JOIN tb_customer_info ON tb_customer_info.id = tb_sell.customer_id
tb_inventory_info.name LIKE CONCAT('%', #{inventoryName},'%') AND tb_customer_info.name LIKE CONCAT('%', #{customerName},'%') AND tb_sell.time > #{beginTime} AND tb_sell.time <= #{endTime}
LIMIT #{offset}, #{size} ORDER BY tb_sell.time DESC
