mapper.select:
SELECT tb_sell_return.id AS id, tb_inventory_info.name AS inventory_name, tb_customer_info.name AS customer_name, tb_sell_return.unit_cost AS unit_cost, tb_sell_return.amount AS amount, tb_sell_return.sum AS sum, tb_sell_return.time AS time, tb_sell_return.clearing_form AS clearing_form, tb_sell_return.handler AS handler FROM tb_sell JOIN tb_inventory_info ON tb_inventory_info.id = tb_sell_return.inventory_id JOIN tb_customer_info ON tb_customer_info.id = tb_sell_return.customer_id
tb_inventory_info.name LIKE CONCAT('%', #{inventoryName},'%') AND tb_customer_info.name LIKE CONCAT('%', #{customerName},'%') AND tb_sell_return.time > #{beginTime} AND tb_sell_return.time <= #{endTime}
LIMIT #{offset}, #{size} ORDER BY tb_sell_return.time DESC
