mapper.select:
SELECT tb_stock.id, tb_provider_info.name AS provider_name, tb_user.username AS user_username, tb_good_info.name AS good_name, tb_stock.variety_amount, tb_stock.unit_cost, tb_stock.amount, tb_stock.sum, tb_stock.check_conclusion, tb_stock.time, tb_stock.clearing_form, tb_stock.handler FROM tb_stock JOIN tb_provider_info ON tb_provider_info.id = tb_stock.provider_id JOIN tb_user ON tb_user.account = tb_stock.user_id JOIN tb_good_info ON tb_good_info.id = tb_stock.good_id
tb_stock.time >= #{startTime} AND tb_stock.time <= #{endTime} AND tb_provider_info.name LIKE CONCAT('%', #{ProviderName},'%') AND tb_good_info.name LIKE CONCAT('%', #{GoodName},'%') AND tb_user.username LIKE CONCAT('%', #{UserName},'%')
LIMIT #{offset}, #{size}
